import React, { useState, useEffect } from 'react';
import './NovelInput.css';

function NovelInput() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [fileFlag, setFileFlag] = useState(0);
  const [cannotUploadMessageVisible, setCannotUploadMessageVisible] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);

  const isAdvancedUpload = (('draggable' in document.createElement('div')) ||
    ('ondragstart' in document.createElement('div') && 'ondrop' in document.createElement('div'))) &&
    'FormData' in window && 'FileReader' in window;

  const fileChange = (e) => {
    const fileInput = e.target;
    setSelectedFile(fileInput.files[0]);
    setFileFlag(0);
    setUploadProgress(0);
    // other logic...
  };

  const uploadButtonClick = () => {
    if (selectedFile) {
      if (fileFlag === 0) {
        setFileFlag(1);
        let width = 0;
        const id = setInterval(() => {
          if (width >= 390) {
            clearInterval(id);
          } else {
            width += 5;
            setUploadProgress(width);
          }
        }, 50);
      }
    } else {
      setCannotUploadMessageVisible(true);
    }
  };

  useEffect(() => {
    if (isAdvancedUpload) {
      // Add your advanced upload logic here...
    }
  }, [isAdvancedUpload]);

  const removeFileButtonClick = () => {
    setSelectedFile(null);
    setFileFlag(0);
    setCannotUploadMessageVisible(false);
    // other logic...
  };

  const cancelAlertButtonClick = () => {
    setCannotUploadMessageVisible(false);
  };

  return (
    <div className="container">
      {/* ... rest of your JSX ... */}
      <input type="file" onChange={fileChange} className="default_file_input" />
      {/* ... rest of your JSX ... */}
      <button type="button" className="upload_button" onClick={uploadButtonClick}>
        Upload
      </button>
      {/* ... rest of your JSX ... */}
      {cannotUploadMessageVisible && (
        <div className="cannot_upload_message">
          <span className="material-icons-outlined">error</span>
          Please select a file first
          <span className="material-icons-outlined cancel_alert_button" onClick={cancelAlertButtonClick}>
            cancel
          </span>
        </div>
      )}
      {selectedFile && (
        <div className="file_block">
          <div className="file_info">
            <span className="material-icons-outlined file_icon">description</span>
            <span className="file_name">{selectedFile.name}</span> |{' '}
            <span className="file_size">{(selectedFile.size / 1024).toFixed(1)} KB</span>
          </div>
          <span className="material-icons remove_file_icon" onClick={removeFileButtonClick}>
            delete
          </span>
          <div className="progress_bar" style={{ width: `${uploadProgress}px` }}></div>
        </div>
      )}
    </div>
  );
}

export default NovelInput;
