import React, { useState } from 'react';
import './NovelInput.css'
import { FileUploader } from "react-drag-drop-files";
const fileTypes = ["JPG", "PNG", "GIF"];

function NovelInput() {
  const [file, setFile] = useState(null);
  const handleChange = (file) => {
    setFile(file);
  }
    return (
      <div className="container">
        <div className="header">
          <h1><a href="/">NEX</a></h1>
        </div>
        <div className="nex_container">
          <FileUploader handleChange={handleChange} name="file" types={fileTypes} className="upload_container" />
          <button type="button" className="upload_button"> Upload </button>
        </div>
      </div>
    );
}

  export default NovelInput;
