import React, { useState } from 'react';
import './SelectCharacter.css';
import { useNavigate } from 'react-router-dom';
import OpenAI from 'openai';

function SelectCharacter() {
  const [prompt, setPrompt] = useState("");
  const [result, setResult] = useState("");
  const [loading, setLoading] = useState(false);

  const [placeholder, setPlaceholder] = useState(
    "Search for a lion with Paint Brushes painting the mona lisa painting..."
  );

  const openai = new OpenAI({ apiKey: "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" });

  const generateImage = async () => {
    setPlaceholder(`Search ${prompt}..`);
    setLoading(true);

    try {
      const res = await openai.createImage({
        prompt: prompt,
        n: 1,
        size: "512x512",
      });

      setLoading(false);
      setResult(res.data.data[0].url);
    } catch (error) {
      setLoading(false);
      console.error(`Error generating image: ${error.response.data.error.message}`);
    }
  }

  const navigate = useNavigate();
  const goChat = (name) => {
    navigate(`/chat?name=${name}`);
  }
  const characters = [
    { id: 1, name: "Rick", image: "Rick.jpg" },
    { id: 2, name: "Morty", image: "Rick.jpg" },
    { id: 3, name: "Summer", image: "Rick.jpg" },
    { id: 4, name: "Beth", image: "Rick.jpg" },
    { id: 5, name: "Jerry", image: "Rick.jpg" },
    { id: 6, name: "Abadango", image: "Rick.jpg" },
    { id: 7, name: "Abradolf", image: "Rick.jpg" },
    { id: 8, name: "Adjudicator", image: "Rick.jpg" },
  ];

  return (
    <div className="container">
      <div className="header">
        <h1><a href="/">NEX</a></h1>
      </div>
      <div className="dall_container">
        {loading ? (
          <>
            <h3>Generating image... Please Wait...</h3>
          </>
        ) : (
          <>
            <h2>Generate an Image using Open AI API</h2>

            <textarea
              className="app-input"
              placeholder={placeholder}
              onChange={(e) => setPrompt(e.target.value)}
              rows="10"
              cols="100"
            />

            <button onClick={generateImage}>Generate an Image</button>

            {result.length > 0 ? (
              <img className="result-image" src={result} alt="result" />
            ) : (
              <>
              </>
            )}
          </>
        )}
      </div>
      <div className="select_container">
        <h1 className="select_header">SELECT CHARACTER</h1>
        <div className="character_container">
          {characters.map(character => (
            <div key={character.id} className="character_card" onClick={() => goChat(character.name)}>
              <img src={character.image} alt={character.name} />
              <p>{character.name}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default SelectCharacter;
