import React, { useState } from 'react';
import './NovelInput.css';

function NovelInput() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [fileFlag, setFileFlag] = useState(0);
  const [isErrorDisplayed, setIsErrorDisplayed] = useState(false);

  const isAdvancedUpload = (('draggable' in document.createElement('div')) ||
    ('ondragstart' in document.createElement('div') && 'ondrop' in document.createElement('div'))) &&
    'FormData' in window && 'FileReader' in window;

  const fileChange = (e) => {
    const fileInput = document.querySelector(".default_file_input");
    setSelectedFile(fileInput.files[0]);
    setFileFlag(0);
    e.target.value = '';
  };

  const handleUploadButtonClick = () => {
    if (selectedFile) {
      if (fileFlag === 0) {
        setFileFlag(1);
        let width = 0;
        const progressBar = document.querySelector(".progress_bar");
        const uploadButton = document.querySelector(".upload_button");

        const id = setInterval(() => {
          if (width >= 390) {
            clearInterval(id);
            uploadButton.innerHTML = `<span class="material-icons-outlined upload_button_icon"> check_circle </span> Uploaded`;
          } else {
            width += 5;
            progressBar.style.width = `${width}px`;
          }
        }, 50);
      }
    } else {
      setIsErrorDisplayed(true);
    }
  };

  const handleCancelAlertButtonClick = () => {
    setIsErrorDisplayed(false);
  };

  const handleRemoveFileButtonClick = () => {
    setSelectedFile(null);
    setFileFlag(0);
    const uploadedFile = document.querySelector(".file_block");
    uploadedFile.style.cssText = "display: none;";
  };

  const dragEvents = ["drag", "dragstart", "dragend", "dragover", "dragenter", "dragleave", "drop"];
  const draggableFileArea = document.querySelector(".drag_file_container");

  if (isAdvancedUpload) {
    dragEvents.forEach(evt => {
      draggableFileArea.addEventListener(evt, (e) => {
        e.preventDefault();
        e.stopPropagation();
      });
    });

    ["dragover", "dragenter"].forEach(evt => {
      draggableFileArea.addEventListener(evt, () => {
        uploadIcon.innerHTML = 'file_download';
        dragDropText.innerHTML = 'Drop your file here!';
      });
    });

    draggableFileArea.addEventListener("drop", (e) => {
      e.preventDefault();
      e.stopPropagation();

      uploadIcon.innerHTML = 'check_circle';
      dragDropText.innerHTML = 'File Dropped Successfully!';
      document.querySelector(".label").innerHTML = `drag & drop or <span class="browse_files"> <input type="file" class="default_file_input" onchange="fileChange(event)"/> <span class="browse_files_text" style="top: 0px; left: 0px;"> browse file</span> </span>`;
      uploadButton.innerHTML = `Upload`;

      const files = e.dataTransfer.files;
      setSelectedFile(files[0]);
      fileName.innerHTML = files[0].name;
      fileSize.innerHTML = `${(files[0].size / 1024).toFixed(1)} KB`;
      const uploadedFile = document.querySelector(".file_block");
      uploadedFile.style.cssText = "display: flex;";
      progressBar.style.width = 0;
      setFileFlag(0);
    });
  }

  return (
    <div className="container">
      <div className="header">
        <h1><a href="/">NEX</a></h1>
      </div>
      <div className="nex_container">
        <div className="drag_file_container">
          <span className="material-icons-outlined upload_icon"> file_upload </span>
          <h3 className="dynamic_message"> Drag & drop any file here </h3>
          <label className="label"> or <span className="browse_files"> <input type="file" onChange={fileChange}
            className="default_file_input" /> <span
              className="browse_files_text">browse
              file</span> <span>from device</span> </span> </label>
        </div>
        <span className="cannot_upload_message">
          <span className="material-icons-outlined">error</span>
          Please select a file first
          <span className="material-icons-outlined cancel_alert_button" onClick={handleCancelAlertButtonClick}>cancel</span>
        </span>
        <div className="file_block">
          <div className="file_info">
            <span className="material-icons-outlined file_icon">description</span>
            <span className="file_name">{selectedFile ? selectedFile.name : ""}</span> |
            <span className="file_size">{selectedFile ? `${(selectedFile.size / 1024).toFixed(1)} KB` : ""}</span>
          </div>
          <span className="material-icons remove_file_icon" onClick={handleRemoveFileButtonClick}>delete</span>
          <div className="progress_bar"></div>
        </div>
        <button type="button" className="upload_button" onClick={handleUploadButtonClick}> Upload </button>
      </div>
    </div>
  );
}

export default NovelInput;
