import React, { useEffect, useState } from 'react';
import './NovelInput.css'

function NovelInput() {
  const [selectedFile, setSelectedFile] = useState([]);
  const [fileFlag, setFileFlag] = useState(0);
  const uploadIcon = document.querySelector(".upload_icon");
  const dragDropText = document.querySelector(".dynamic_message");
  const uploadButton = document.querySelector(".upload_button");
  const fileName = document.querySelector(".file_name");
  const fileSize = document.querySelector(".file_size");
  const uploadedFile = document.querySelector(".file_block");
  const progressBar = document.querySelector(".progress_bar");
  const cannotUploadMessage = document.querySelector(".cannot_upload_message");

  const isAdvancedUpload = function () {
    var div = document.createElement('div');
    return (('draggable' in div) || ('ondragstart' in div && 'ondrop' in div)) && 'FormData' in window && 'FileReader' in window;
  }();

  const onSelectFile = (e) => {
    e.preventDefault();
    const fileInput = document.querySelector(".default_file_input");
    setSelectedFile(fileInput.files[0]);
    uploadIcon.innerHTML = 'check_circle';
    dragDropText.innerHTML = 'File Dropped Successfully!';
    document.querySelector(".label").innerHTML = `drag & drop or <span class="browse_files"> <input type="file" class="default_file_input" onChange={onSelectFile}/> <span class="browse_files_text" style="top: 0;"> browse file</span></span>`;
		uploadButton.innerHTML = `Upload`;
    fileName.innerHTML = fileInput.files[0].name;
    fileSize.innerHTML = (fileInput.files[0].size / 1024).toFixed(1) + " KB";
    uploadedFile.style.cssText = "display: flex;";
    progressBar.style.width = 0;
    setFileFlag(0);
    e.target.value = ''
  }

  const fileUpload = () => {
    let isFileUploaded = selectedFile;
    if (isFileUploaded !== '') {
      if (fileFlag === 0) {
        console.log(selectedFile);
        setFileFlag(1);
        var width = 0;
        var id = setInterval(frame, 50);
        function frame() {
          if (width >= 390) {
            clearInterval(id);
            uploadButton.innerHTML = `<span class="material-icons-outlined upload_button_icon"> check_circle </span> Uploaded`;
          } else {
            width += 5;
            progressBar.style.width = width + "px";
          }
        }
      }
    } else {
      cannotUploadMessage.style.cssText = "display: flex; animation: fadeIn linear 1.5s;";
    }
  }

  const canelAlert = () => {
    cannotUploadMessage.style.cssText = "display: none;";
  }

  const fileRemove = () => {
    uploadedFile.style.cssText = "display: none;";
    setSelectedFile('');
    fileName.innerHTML = null;
    fileSize.innerHTML = null;
    uploadIcon.innerHTML = 'file_upload';
    dragDropText.innerHTML = 'Drag & drop any file here';
    document.querySelector(".label").innerHTML = `or <span class="browse_files"> <input type="file" class="default_file_input onChange={onSelectFile}/> <span class="browse_files_text">browse file</span> <span>from device</span> </span>`;
    uploadButton.innerHTML = `Upload`;
    document.querySelector(".label").innerHTML = `or <span class="browse_files"> <input type="file" class="default_file_input" onChange={onSelectFile}/> <span class="browse_files_text">browse file</span> <span>from device</span> </span>`;
  }

  useEffect(() => {
    if (isAdvancedUpload) {
      const draggableFileArea = document.querySelector(".drag_file_container");

      if (draggableFileArea) {
        ["drag", "dragstart", "dragend", "dragover", "dragenter", "dragleave", "drop"].forEach(evt =>
          draggableFileArea.addEventListener(evt, e => {
            e.preventDefault();
            e.stopPropagation();
          })
        );
      
        ["dragover", "dragenter"].forEach(evt => {
          draggableFileArea.addEventListener(evt, e => {
            e.preventDefault();
            e.stopPropagation();
            uploadIcon.innerHTML = 'file_download';
            dragDropText.innerHTML = 'Drop your file here!';
          });
        });
      
        draggableFileArea.addEventListener("drop", e => {
          uploadIcon.innerHTML = 'check_circle';
          dragDropText.innerHTML = 'File Dropped Successfully!';
          document.querySelector(".label").innerHTML = `drag & drop or <span class="browse_files"> <input type="file" class="default_file_input" onChange={onSelectFile}/> <span class="browse_files_text" style="top: 0px; left: 0px;"> browse file</span> </span>`;
          uploadButton.innerHTML = `Upload`;
      
          let files = e.dataTransfer.files;
          setSelectedFile(files[0]);
          console.log(files[0].name + " " + files[0].size);
          fileName.innerHTML = files[0].name;
          fileSize.innerHTML = (files[0].size / 1024).toFixed(1) + " KB";
          uploadedFile.style.cssText = "display: flex;";
          progressBar.style.width = 0;
          setFileFlag(0);
        });
      }
    }
  }, [dragDropText, fileName, fileSize, isAdvancedUpload, progressBar.style, uploadButton, uploadIcon, uploadedFile.style]);

  if (progressBar) {
    progressBar.style.width = 0;
  }
  

  return (
    <div className="container">
      <div className="header">
        <h1><a href="/">NEX</a></h1>
      </div>
      <div className="nex_container">
        <div className="drag_file_container">
          <span className="material-icons-outlined upload_icon"> file_upload </span>
          <h3 className="dynamic_message"> Drag & drop any file here </h3>
          <label className="label"> or <span className="browse_files"> <input type="file" onChange={onSelectFile}
            className="default_file_input" /> <span
              className="browse_files_text">browse
              file</span> <span>from device</span> </span> </label>
        </div>
        <span className="cannot_upload_message"> <span className="material-icons-outlined">error</span>
          Please select a file first <span
            className="material-icons-outlined cancel_alert_button" onClick={canelAlert}>cancel</span> </span>
        <div className="file_block">
          <div className="file_info"> <span
            className="material-icons-outlined file_icon">description</span> <span
              className="file_name"> </span> | <span className="file_size"> </span> </div>
          <span className="material-icons remove_file_icon" onClick={fileRemove}>delete</span>
          <div className="progress_bar"> </div>
        </div>
        <button type="button" className="upload_button" onClick={fileUpload}> Upload </button>
      </div>
    </div>
  );
}

export default NovelInput;
